(window.webpackJsonp=window.webpackJsonp||[]).push([[25],{413:function(v,_,o){"use strict";o.r(_);var t=o(11),p=Object(t.a)({},(function(){var v=this,_=v.$createElement,o=v._self._c||_;return o("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[o("p",[v._v("动物农场\n乔治·奥威尔\n32个想法")]),v._v(" "),o("p",[v._v("◆ 第一章")]),v._v(" "),o("blockquote",[o("p",[v._v("我懂得在这片土地上生活而且懂得不比如今活着的任何动物差")])]),v._v(" "),o("blockquote",[o("p",[v._v("我们的生命是悲惨的、劳苦的和短促的。我们生了下来，供给我们的食物仅仅够维持我们的躯体里始终有一口气，我们当中那些能活下来的，就被强迫干活，直到筋疲力尽；一旦我们的使用价值到了尽头，我们立马就会遭到骇人听闻的残酷杀戮。")])]),v._v(" "),o("blockquote",[o("p",[v._v("要是有谁对你说，人和动物有共同利益，人富即动物富，那都是谎话，绝对听不得。人只为自己谋利益，不为其他任何动物谋利益。我们动物之间在斗争中必须完全团结一致，建立真正的同志情谊。所有的人都是我们的仇敌。所有的动物都是同志。")])]),v._v(" "),o("blockquote",[o("p",[v._v("最最重要的是，动物不得欺压自己的同类。不分强弱，无论贤愚，我们都是兄弟。凡动物都不可杀任何别的动物。凡动物一律平等。")])]),v._v(" "),o("p",[v._v("◆ 第四章")]),v._v(" "),o("blockquote",[o("p",[v._v("开始谈论目下在动物农场可怕的兽行妖风甚炽。据称那里的动物已在不折不扣地食同类之肉，用烧红的马蹄铁互相施虐，对雌性配偶实行共有。这是违背自然法则悍然造反带来的必然结果，弗雷德里克和皮尔金顿如是说。")])]),v._v(" "),o("blockquote",[o("p",[v._v("“战争就是战争。只有一种人是好的，那就是死人。”")])]),v._v(" "),o("p",[v._v("◆ 第五章")]),v._v(" "),o("blockquote",[o("p",[v._v("凶相十足，像一群恶狼。他们紧挨在拿破仑身边。有动物注意到，他们朝着拿破仑摇尾巴的神态，跟另一些狗过去惯于向琼斯先生做的姿态一个样。")])]),v._v(" "),o("blockquote",[o("p",[v._v("吱嘎私下向另外一些动物透露，拿破仑其实从来没有反对这风车计划。相反，正是他一开始力主建造风车，而雪球曾经画在孵化器棚内地上的草图实际上是从拿破仑的文档中偷走的。")])]),v._v(" "),o("p",[v._v("◆ 第六章")]),v._v(" "),o("blockquote",[o("p",[v._v("那一年动物们自始至终像奴隶一般在干活。但他们干得舒心；他们舍得出力，不怕牺牲，清楚地意识到自己做的一切无不为了他们自己的福祉，也是为了他们同类及其后代的福祉，而不是为了一帮不劳而获、专事偷盗的人。")])]),v._v(" "),o("p",[v._v("◆ 第七章")]),v._v(" "),o("blockquote",[o("p",[v._v("向外界隐瞒这一事实，乃是生死攸关的要务。")])]),v._v(" "),o("blockquote",[o("p",[v._v("动物们给彻底吓坏了。他们觉得雪球仿佛成了某种无形的影响力，弥漫在他们周围的空气中，制造出各种各样的危险令他们防不胜防。")])]),v._v(" "),o("blockquote",[o("p",[v._v("雪球从一开始就和琼斯穿的是连裆裤！他一直是琼斯的暗藏特务。这一切从他没能带走而且刚刚被我们发现的文件中得到了证实。")])]),v._v(" "),o("blockquote",[o("p",[v._v("“我怎么也想不明白。我实在没法相信这等事会发生在我们农场。一定是我们自己在什么地方出了纰漏。据我看来，解决的办法只有一个——更加努力工作。从今以后，每天早晨我要提前整整一小时起身。”")])]),v._v(" "),o("blockquote",[o("p",[v._v("理想的动物社会没有盼到，而他们反倒落入了这样一个时代：谁也不敢说出自己的想法，动辄狂吠不止的恶犬到处横行，你不得不眼睁睁看着你的同志在招认了丑恶罪行后被撕成碎片—")])]),v._v(" "),o("p",[v._v("◆ 第八章")]),v._v(" "),o("blockquote",[o("p",[v._v("星期天上午，吱嘎总要用蹄子夹着长长一条纸，向动物们宣读大串大串的数字，表明各档粮食的产量分别增长百分之二百，百分之三百或百分之五百，因不同情况而异。动物们认为没有理由不相信他，何况他们再也记不清造反之前究竟是怎么个状况。反正有些日子他们还是感到，他们宁愿少听些数字，多得到些吃的。")])]),v._v(" "),o("blockquote",[o("p",[v._v("从撬棍地农场不断有可怕的传闻渗漏出来，说弗雷德里克一直在他的动物们身上实验种种残忍的虐待手段。他曾鞭打一匹衰老的马致死，他让他的母牛们挨饿，他把一条狗扔进火炉活活烧死，他每天晚上把破损的刀片缚在公鸡后爪上挑动他们互斗取乐。动物们听到竟有人如此荼毒他们的同志，无不义愤填膺，热血沸腾，有几回曾主动请缨，嚷着要求让他们倾巢出动，兵发撬棍地农场，把人们统统赶走，解放那里的所有动物。但是吱嘎劝说他们避免采取过激行动，要充分信任拿破仑同志的策略高明。")])]),v._v(" "),o("blockquote",[o("p",[v._v("他以个人的名义为动物们取得的成就向他们表示祝贺，并宣布风车被命名为拿破仑风车。")])]),v._v(" "),o("blockquote",[o("p",[v._v("看到了这幅景象，动物们的勇气反而又回到他们身上。片刻之前他们感到的恐惧和绝望，已淹没在由敌人可鄙可耻的行径所激起的狂怒之中。")])]),v._v(" "),o("p",[v._v("◆ 第九章")]),v._v(" "),o("blockquote",[o("p",[v._v("难以做出判断的倒是猪们对待摩西的态度。猪们全都以不屑的口气宣布摩西所讲关于糖果山的故事纯属胡编乱造，然而他们又允许他留在农场，什么活也不干，每天还可得到七分之一升啤酒的津贴。")])]),v._v(" "),o("blockquote",[o("p",[v._v("“‘阿尔弗雷德·西蒙兹，屠马兼熬胶，住维林敦镇。经销兽皮和骨粉。可为养犬客户送货上门。’你们可懂得那是什么意思？他们要把拳击手拉到屠马作坊去！”")])]),v._v(" "),o("blockquote",[o("p",[v._v("他唯一的遗憾就是走在风车竣工之前。‘前进，同志们！’他贴在我耳边说。‘以造反的名义，前进。动物农场万岁！拿破仑同志万岁！拿破仑永远正确！’这是他最后的几句话，同志们。”")])]),v._v(" "),o("blockquote",[o("p",[v._v("拿破仑在结束他的演说时引用了拳击手心爱的两句格言。“‘我会更加努力工作’和‘拿破仑同志永远正确’这两句格言，”他说，“我奉劝每一只动物最好都把它们当成自己的座右铭。”")])]),v._v(" "),o("p",[v._v("◆ 第十章")]),v._v(" "),o("blockquote",[o("p",[v._v("拿破仑如今是一头重达三百三十磅的成熟公猪。吱嘎胖得几乎睁不开眼睛。唯独老本杰明大体上还是过去的模样，只是鼻口处的毛色稍增灰白，还有就是自打拳击手死后越发孤僻自闭，寡言少语。")])]),v._v(" "),o("blockquote",[o("p",[v._v("拿破仑指责这种想法是与动物主义的精神背道而驰的。他说，真真正正的幸福就在于勤奋的工作和俭朴的生活。")])]),v._v(" "),o("blockquote",[o("p",[v._v("只有老本杰明表示自己漫长一生的每一个细节他都记得起来，也知道事情从来没有、也永远不会大大好于过去或大大不如过去——反正饥饿、辛苦和失望是生活的不变法则，他如是说。")])]),v._v(" "),o("blockquote",[o("p",[v._v("“四条腿好，两条腿更好！四条腿好，两条腿更好！四条腿好，两条腿更好！”")])]),v._v(" "),o("blockquote",[o("p",[v._v("他呛了好一阵子，在这期间他那呈多级台阶状的下巴转成了紫色，呛过以后，他总算说出了口：“你们有你们的低等动物需要对付，”他说，“我们有我们的下层阶级需要摆平！”这句妙语一出，举座为之笑得前俯后仰；")])]),v._v(" "),o("blockquote",[o("p",[v._v("因为他，拿破仑，现在才第一次正式宣布此事：“动物农场”这个名称已经废除。今后农场将被称为“庄园农场”——他相信这才是农场正确的原名。")])]),v._v(" "),o("blockquote",[o("p",[v._v("敢情动物们从窗外朝里望，目光从猪移到人，再从人移到猪，又重新从猪移到人，要分清哪张脸是猪的，哪张脸是人的，已经不可能了。")])]),v._v(" "),o("p",[v._v("◆ 附录：《动物农场》乌克兰文版序")]),v._v(" "),o("blockquote",[o("p",[v._v("我所以成为拥护社会主义者主要是出于对产业工人中比较穷困的一部分受到压迫和忽视的情况感到厌恶，而不是出于对计划社会有什么理论上的想望。")])]),v._v(" "),o("blockquote",[o("p",[v._v("如果这些牲口知道它们自己的力量，我们就无法控制它们，人类剥削牲口就像富人剥削无产阶级一样。")])])])}),[],!1,null,null,null);_.default=p.exports}}]);